class Buttons extends Component{



    nextPage =() => {

        this.setState({
          i: this.props.i +1



        })
      }

      lastPage =() => {

        this.setState({
          i: this.props.i -1



        })
      }

    render(){
        return(

     <div>
     <button className='button2' onClick={this.lastPage}>&lt; Previous</button>
     <div>
     <button className='button1'>Edit</button>
     <button className='button1'>Delete</button>
     <button className='button1'>New</button>
     </div>
     <button className='button2' onClick= {this.nextPage}>Next &gt;</button>
    </div>

        )

    }
}

export default Buttons
